mapper.select:
select ID, `NAME`, STATUS, PROJECTID, projectname, LISTID, CREATETIME, UPDATETIME, USERID, USERNAME from (select a.ID, a.NAME, a.STATUS, a.PROJECTID, b.name projectname, a.LISTID, a.CREATETIME, a.UPDATETIME, a.USERID, a.USERNAME from base_customer a,base_project b where a.projectid=b.id
and a.status=#{status,jdbcType=INTEGER}
and a.name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')
and b.name like CONCAT('%',#{project,jdbcType=VARCHAR},'%') ) m
ORDER BY PROJECTID,listid
ORDER BY ${order} ${sort}
LIMIT #{limit} OFFSET #{offset}

mapper.insert:
insert into base_customer (ID, PROJECTID, NAME,
  STATUS, LISTID, CREATETIME, 
  UPDATETIME, USERID, USERNAME
  )
values (#{id,jdbcType=INTEGER}, #{projectid,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{listid,jdbcType=INTEGER}, #{createtime,jdbcType=VARCHAR},
  #{updatetime,jdbcType=VARCHAR}, #{userid,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}
  )
