mapper.select:
select count(id) from sys_role where 1=1
and status=#{status,jdbcType=INTEGER}
and name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')

select 

from sys_role
where 1=1

	and status=#{status,jdbcType=INTEGER}


	and name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')


	ORDER BY listid


	ORDER BY ${order} ${sort}

LIMIT #{limit} OFFSET #{offset}

mapper.insert:
insert into sys_role (ID, NAME, STATUS, 
  LISTID, CREATETIME, UPDATETIME, 
  USERID, USERNAME, REMARK
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, 
  #{listid,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}, 
  #{userid,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )
